tinymce.PluginManager.add("ecgallery",function(editor,hst){
	editor.addButton("ecgallery",{
		text : "Galeri",
		icon : false,
		onclick : function(){
			editor.windowManager.open({
				title : "Galeri",
				url : hst + "/gallery_modal.jsp",
				width : 600,
				height : 400,
				buttons : [{
					text : "Ekle",
					onclick : function(){
						//console.log(editor.windowManager.getWindows()[0].getContentWindow().selectedMaterials);
						var b = editor.windowManager.getWindows()[0];
						var materials = b.getContentWindow().selectedMaterials;
						//console.log(window.frames[1]);
						//console.log(b.getContentWindow().selectedMaterials);
						//console.log(materials);
						for(var i=0;i<materials.length;i++){
							console.log(materials[i].path);
							editor.insertContent('<img src="'+materials[i].filePath+'" />');
						}
						b.close();
						/*editor.insertContent(b.getContentWindow().document
								.getElementById("ender").value),
								b.close()*/
						}
					},{
						text : "Kapat",
						onclick : "close"
					}]
			})
		}
	})
});